(68, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Bảo vệ Của hàng WooCommerce của bạn bằng Jetpack Backup.', 'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.', '{}', 'unactioned', 'woocommerce-admin-notes', '2023-07-17 05:03:31', NULL, 0, 'thumbnail', 'http://localhost/wordpress/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info'),
(69, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2023-07-17 05:03:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(70, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2023-07-24 06:23:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(71, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Help WooCommerce improve with usage tracking', 'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://localhost/wordpress/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking?utm_medium=product\" target=\"_blank\">Read more</a> about what data we collect.', '{}', 'unactioned', 'woocommerce-admin', '2023-07-24 06:23:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'wc-admin-woocommerce-payments', 'marketing', 'en_US', 'Try the new way to get paid', 'Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with <strong>WooCommerce Payments</strong>.<br><br>By clicking \"Get started\", you agree to our <a href=\"https://wordpress.com/tos/\" target=\"_blank\">Terms of Service</a>', '{}', 'unactioned', 'woocommerce-admin', '2023-07-24 06:23:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(73, 'square_payments_q3_2023', 'marketing', 'en_US', 'Square for WooCommerce', 'Connect your Square account with WooCommerce to accept online and in-person payments (including contactless payments and cross-channel gift cards) quickly and securely. Save time by syncing sales, customers, and items and inventory — and manage everything through one centralized platform.', '{}', 'pending', 'woocommerce.com', '2023-07-24 06:23:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(74, 'wc-admin-woopayments-rebrand', 'marketing', 'en_US', 'WooCommerce Payments is now WooPayments', 'We’ve changed the name as part of recent updates to the Woo brand. You’ll still enjoy the same seamless payments experience — just look for WooPayments in your WordPress admin.', '{}', 'pending', 'woocommerce.com', '2023-07-31 08:58:24', NULL, 0, 'plain', '', 0, 0, 'info');
